CREATE TABLE spouses (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT,
    description TEXT,
    image TEXT
);

INSERT INTO spouses (name, description, image) VALUES ("Fushiguro Megumi", "The deuteragonist of the Jujutsu Kaisen series. He is a first-year student at Tokyo Jujutsu High and also a descendant of the Zenin family.", "https://static.wikia.nocookie.net/debatesjungle/images/f/ff/Megumi_Fushiguro_%25_28Anime_2%25_29.png");
INSERT INTO spouses (name, description, image) VALUES ("Albedo", "The Overseer of the Guardians of the Great Tomb of Nazarick. She is in charge of the general management and supervision of the activities done by the seven Floor Guardians, meaning that she ranks above the other NPCs in Nazarick.", "https://static.wikia.nocookie.net/overlordmaruyama/images/c/c6/Albedo_Anime_Updated.png");
INSERT INTO spouses (name, description, image) VALUES ("Maki Zenin", "A major supporting character in Jujutsu Kaisen and one of the main protagonists of its prequel, Jujutsu Kaisen 0: Jujutsu High. She is currently a second-year at Tokyo Jujutsu High. Daughter of Ogi Zenin and the elder twin sister of Mai Zenin, Maki was born in the Zenin Family.", "https://static.wikia.nocookie.net/jujutsu-kaisen/images/5/55/Maki_Zen%27in_%28Anime%29.png");
